        ac.jLabelYUp(10, -240, 25, 5, lbluser);
        ac.jTextFieldYDown(-240, 50, 25, 5, txtusuario);
        ac.jTextFieldYUp(50, -240, 25, 5, txtusuario);
        ac.jLabelYDown(-240,90, 25, 5, lblpassword);
