    private void btniniciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btniniciarActionPerformed
       String busqueda_usuario=metodos.BuscarUsuarioRegistrado(txtusuario.getText(), jtxtpcont.getText());
       if(busqueda_usuario.equals("usuario encontrado")){
          
          this.dispose();
          String busqueda_nombre=metodos.BuscarNombre(txtusuario.getText());
          getToolkit().beep();
          JOptionPane.showMessageDialog(this,"BIENVENIDO(A) \n"+busqueda_nombre);
       }else{
            Toolkit.getDefaultToolkit().beep();
           JOptionPane.showMessageDialog(this,"USUARIO O CONTRASEÃ‘A INVALIDOS");
       
       }
       txtusuario.setText("");
       jtxtpcont.setText("");
    }//GEN-LAST:event_btniniciarActionPerformed

