    private void jcboxMostarPassActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jcboxMostarPassActionPerformed
        if(jcboxMostarPass.isSelected()){
            jtxtpcont.setEchoChar((char)0);
        }else{
            jtxtpcont.setEchoChar('*');
        }
    }//GEN-LAST:event_jcboxMostarPassActionPerformed

     
   

